const f=(r,s=1)=>{const n=/^#?([a-f\d])([a-f\d])([a-f\d])$/i;r=r.replace(n,(b,o,c,F)=>o+o+c+c+F+F);const t=/^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(r);if(!t)return`rgba(0, 0, 0, ${s})`;const[e,a,g]=[parseInt(t[1],16),parseInt(t[2],16),parseInt(t[3],16)];return`rgba(${e}, ${a}, ${g}, ${s})`},i=r=>{const s=r.replace("#","");if(s.length!==6)return"#FFFFFF";const n=parseInt(s.substring(0,2),16),t=parseInt(s.substring(2,4),16),e=parseInt(s.substring(4,6),16);return(n*299+t*587+e*114)/1e3>150?"#000000":"#FFFFFF"};export{f as h,i as s};
